#ifndef SETTINGS_HTML_H
#define SETTINGS_HTML_H

const char* htmlContent = R"(
<html>
<head>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            background-color: #f4f4f4;
            color: #333;
            font-size: 2em;
        }

        h1 {
            text-align: center;
            color: #444;
        }

        p {
            margin: 15px;
        }

        input[type='range'] {
            width: 100%;
            margin: 50px 0;
            -webkit-appearance: none; /* Override default appearance */
            height: 15px; /* Increased thickness for the slider */
            background: #ddd; /* Slider background */
            outline: none; /* Remove outline */
        }

        input[type='range']::-webkit-slider-thumb {
            -webkit-appearance: none;
            appearance: none;
            width: 50px; /* Increased width of the slider thumb */
            height: 50px; /* Increased height of the slider thumb */
            background: #333; /* Slider thumb color */
            cursor: pointer; /* Cursor on hover */
        }

        input[type='range']::-moz-range-thumb {
            width: 50px; /* For Firefox */
            height: 50px; /* For Firefox */
            background: #333; /* For Firefox */
            cursor: pointer; /* For Firefox */
        }

        @media (max-width: 600px) {
            body {
                font-size: 86px; /* Smaller font size for smaller screens */
            }

            h1 {
                font-size: 2em; /* Larger header on smaller screens */
            }

            p {
                font-size: 1em; /* Adjust paragraph font size */
                margin: 15px;
                display: flex; /* Use flexbox for layout */
                align-items: center; /* Align items vertically */
            }

            p span {
                margin-left: 10px; /* Space between slider and its value */
            }
        }

        .selector {
            text-align: center;
            padding: 20px;
        }

        .smiley {
            display: inline-block;
            margin-right: 15px;
            cursor: pointer;
            vertical-align: middle;
        }

        .smiley-icon {
            font-size: 5em; /* Large smileys */
            display: block;
        }

        input[type="radio"] {
            display: none; /* Hide radio button */
        }

        input[type="radio"]:checked + .smiley-icon {
            border: 10px solid blue; /* Highlight selected smiley */
            border-radius: 20px; /* Rounded corners for the border */
            padding: 20px;
        }

    </style>
    <script>
        function sendData() {
            // Get the values
            var flameHeight = document.getElementById('FlameHeight').value;
            var sparks = document.getElementById('Sparks').value;
            var delayDuration = document.getElementById('DelayDuration').value;
            var eyeBrightness = document.getElementById('EyeBrighntess').value;
            var selMode = document.querySelector('input[name="selection"]:checked').value;

            // Update the display elements
            document.getElementById('FlameHeightValue').textContent = flameHeight;
            document.getElementById('SparksValue').textContent = sparks;
            document.getElementById('DelayDurationValue').textContent = delayDuration;
            document.getElementById('EyeBrighntess').textContent = eyeBrightness;

            // Send the data
            var xhr = new XMLHttpRequest();
            xhr.open('GET', '/update?FlameHeight=' + flameHeight
                + '&Sparks=' + sparks + '&DelayDuration=' + delayDuration
                + '&EyeBrightness=' + eyeBrightness
                + '&SelMode=' + selMode, true);
            xhr.send();
        }

        // Function to initialize the display values on page load
        function initializeValues() {
            document.querySelector('input[name="selection"][value="fire"]').checked = true;
            sendData(); // This will update the display elements with the initial values
        }

        document.addEventListener('DOMContentLoaded', function() {
            var FlameHeightSlider = document.getElementById('FlameHeight');
            var output = document.getElementById('FlameHeightValue');

            FlameHeightSlider.oninput = function() {
                output.innerHTML = this.value;
            }
        });

        document.addEventListener('DOMContentLoaded', function() {
            var SparksSlider = document.getElementById('Sparks');
            var output = document.getElementById('SparksValue');

            SparksSlider.oninput = function() {
                output.innerHTML = this.value;
            }
        });

        document.addEventListener('DOMContentLoaded', function() {
            var DelaySlider = document.getElementById('DelayDuration');
            var output = document.getElementById('DelayDurationValue');

            DelaySlider.oninput = function() {
                output.innerHTML = this.value;
            }
        });

        document.addEventListener('DOMContentLoaded', function() {
            var DelaySlider = document.getElementById('EyeBrightness');
            var output = document.getElementById('EyeBrightnessValue');

            DelaySlider.oninput = function() {
                output.innerHTML = this.value;
            }
        });
    </script>
</head>
<body onload='initializeValues()'>
    <h1>Phoenix LED</h1>
    <p>Flammenhöhe/Farbe: <span id='FlameHeightValue'></span>
        <input type='range' id='FlameHeight' min='0' max='500' value='{{FlameHeight}}' onchange='sendData()'>
    </p>
    <p>Funken/Sättigung: <span id='SparksValue'></span>
        <input type='range' id='Sparks' min='0' max='255' value='{{Sparks}}' onchange='sendData()'>
    </p>
    <p>Geschwindigkeit: <span id='DelayDurationValue'></span>
        <input type='range' id='DelayDuration' min='0' max='100' value='{{DelayDuration}}' onchange='sendData()'>
    </p>
    <p>Helligkeit Augen: <span id='EyeBrightnessValue'></span>
        <input type='range' id='EyeBrightness' min='0' max='255' value='{{EyeBrightness}}' onchange='sendData()'>
    </p>
    <div class="selector">
        <label class="smiley">
            <input type="radio" name="selection" value="fire" onchange='sendData()'>
            <img src=" data:image/png;base64," alt="Fire" class="smiley-icon">
        </label>
        <label class="smiley">
            <input type="radio" name="selection" value="rainbow_pony" onchange='sendData()'>
            <img src=" data:image/png;base64," alt="Unicorn" class="smiley-icon">
        </label>
        <label class="smiley">
            <input type="radio" name="selection" value="star" onchange='sendData()'>
            <img src=" data:image/png;base64," alt="Star" class="smiley-icon">
        </label>
        <label class="smiley">
            <input type="radio" name="selection" value="sun" onchange='sendData()'>
            <img src=" data:image/png;base64," alt="sun" class="smiley-icon">
        </label>
        <label class="smiley">
            <input type="radio" name="selection" value="ape" onchange='sendData()'>
            <img src=" data:image/png;base64," alt="ape" class="smiley-icon">
        </label>
    </div>
</body>
</html>

)";

#endif /* SETTINGS_HTML_H */
